@module({
	controllers:[ SimpleController ],
	services:[ SimpleService ],
	imports: [ DatabaseModule ],
	exports: [ SimpleService ]
})
export default class SimpleModule(){}

const Home = ({products}: Props) => {
  return <>{
    products.map((product)=><ProductCard data={product}/>)
  }</>
}

interface Props {
  products: Products[]
}

export const getStaticProps: GetStaticProps<Props> = async () => {
  const products = await api.get("http://backend.io/producets");
  return {
    props: {
      products: products,
    },
    revalidate: 1
  }
}

export default Home;

const fs = require("fs");
const path = require("path");

const node_modules = {};
fs.readdirSync("./node_modules")
    .filter((name) => {
        return [".bin"].indexOf(name) === -1;
    })
    .forEach((name) => {
        node_modules[name] = `commonjs ${name}`;
    });

module.exports = {
    entry: "./index.js",
    target: "node",
    output: {
        path: path.join(__dirname, "build"),
        filename: "bundle.js",
    },
    externals: node_modules,
};

// install webpack
npm install webpack webpack-cli

// build bundle with webpack.config.js
npx webpack

// install bytenode
npm install bytenode

// build bytecodes with bytenode
npx bytenode ./build/bundle.js

